require "set"

signal = ""
# signal = "mjqjpqmgbljsphdztnvjfqwrcgsmlb"

def marker?(value)
  Set.new(value.chars).size == value.size
end

def find_result(signal, window)
  signal.chars.each_index do |i|
    value = signal[i...i + window]
    if marker?(value)
      puts value, i + window
      break
    end
  end
end

find_result(signal, 4)
find_result(signal, 14)
